00080         static void increment(int[] key, int[] r)
00081         {
00082             int temp1 = key[1];
00083             if (key[1] == symbols[r[1]][0] || key[1] == symbols[r[1]][1])
00084             {
00085                 key[0] = (key[0] + 1) % 26;
00086                 key[1] = (key[1] + 1) % 26;
00087             }
00088             if (key[2] == symbols[r[2]][0] || key[2] == symbols[r[2]][1])
00089             {
00090                 if (temp1 == key[1])
00091                     key[1] = (key[1] + 1) % 26;
00092             }
00093             key[2] = (key[2] + 1) % 26;
00094         }
