00127         static char enigma(char symbol, int[] key, int[] _rotors, int[] ring, string reflector, Dictionary<char, char> replc)
00128         {
00129             if (replc.ContainsKey(symbol))
00130                 symbol = replc[symbol];
00131 
00132             bool m4 = false;
00133             int rotor4 = 0, ring4 = 0, key4 = 0;
00134 
00135             if (_rotors.Length > 3)
00136             {
00137                 m4 = true;
00138                 key4 = key[3];
00139                 key = new[] { key[0], key[1], key[2] };
00140                 rotor4 = _rotors[3];
00141                 _rotors = new[] { _rotors[0], _rotors[1], _rotors[2] };
00142                 ring4 = ring[3];
00143                 ring = new[] { ring[0], ring[1], ring[2] };
00144             }
00145 
00146             int i = _rotors.Length - 1;
00147             while (i >= 0)
00148             {
00149                 symbol = rotor(symbol, rotors[_rotors[i]], key[i] - ring[i]);
00150                 i--;
00151             }
00152 
00153             if (m4) symbol = rotor(symbol, rotors[rotor4], key4 - ring4);
00154             symbol = sub(symbol, reflector.ToCharArray());
00155             if (m4) symbol = rotor(symbol, reverse(rotors[rotor4]), key4 - ring4);
00156 
00157             i = 0;
00158             while (i < _rotors.Length)
00159             {
00160                 symbol = rotor(symbol, reverse(rotors[_rotors[i]]), key[i] - ring[i]);
00161                 i++;
00162             }
00163 
00164             if (replc.ContainsKey(symbol))
00165                 symbol = replc[symbol];
00166             return symbol;
00167         }
