00176         static char rotor(char symbol, string rotor, int offset)
00177         {
00178             int symbolCode = (code(symbol) + 26 + offset) % 26;
00179             return (char)((code(rotor[symbolCode]) + 26 - offset) % 26 + 65);
00180         }
