00286             Rectangle pos1 = new Rectangle(position.X, position.Y, position.Width, position.Height - 58);
00287             Rectangle pos2 = new Rectangle(position.X, position.Y + 58, position.Width, position.Height - 58);
00288             if (pos1.Contains(mouseOne.X, mouseOne.Y) && mouseOne.LeftButton == ButtonState.Pressed && mouseTwo.LeftButton == ButtonState.Released && isSettings)
00289             {
00290                 enigma.BackAt(rotor);
00291                 sliderPositions[rotor] = sliderPositions[rotor] == 0 ? 1 : 0;
00292                 soundUp.Play(volume, 0, 0);
00293             }
00294             else if (pos2.Contains(mouseOne.X, mouseOne.Y) && mouseOne.LeftButton == ButtonState.Pressed && mouseTwo.LeftButton == ButtonState.Released && isSettings)
00295             {
00296                 enigma.MoveAt(rotor);
00297                 sliderPositions[rotor] = sliderPositions[rotor] == 0 ? 1 : 0;
00298                 soundUp.Play(volume, 0, 0);
00299             }
00300             spriteBatch.Draw(sliders[sliderPositions[rotor]], position, Color.White);
00301         }
