00402         char waitSymbol = '\0';
00403         void drawPlug(int x, int y, char symbol)
00404         {
00405             Rectangle position = new Rectangle(x, y, 18, 71);
00406             if (position.Contains(mouseOne.X, mouseOne.Y) && mouseOne.LeftButton == ButtonState.Pressed && mouseTwo.LeftButton == ButtonState.Released)
00407             {
00408                 if (isWait)
00409                 {
00410                     if (symbol == waitSymbol)
00411                         charset.Remove(symbol);
00412                     else if (replace.ContainsKey(symbol))
00413                     {
00414                         charset.Remove(waitSymbol);
00415                     }
00416                     else
00417                     {
00418                         replace[symbol] = waitSymbol;
00419                         replace[waitSymbol] = symbol;
00420                         charset.Remove(waitSymbol);
00421                         charset.Remove(symbol);
00422                     }
00423 
00424                     waitSymbol = '\0';
00425                     isWait = false;
00426                 }
00427                 else
00428                 {
00429                     if (replace.ContainsKey(symbol))
00430                     {
00431                         replace.Remove(replace[symbol]);
00432                         replace.Remove(symbol);
00433                     }
00434                     else
00435                     {
00436                         isWait = true;
00437                         waitSymbol = symbol;
00438                         charset.Add(waitSymbol);
00439                     }
00440                 }
00441                 soundTick.Play(volume, -1, 0);
00442             }
00443 
00444             int val = BaseEnigma.code(symbol) + 1;
00445             string ssym = isNumber ? val.ToString() : symbol.ToString();
00446             int plused = isNumber ? (val < 10 ? 0 : -5) : (symbol == 'I' ? 5 : 0);
00447 
00448             if (isWait)
00449             {
00450                 if (symbol == waitSymbol)
00451                     spriteBatch.DrawString(fontBase, ssym, new Vector2(x + plused, y - 4), Color.White);
00452                 else
00453                     spriteBatch.DrawString(fontBase, ssym, new Vector2(x + plused, y - 4), Color.Gray);
00454             }
00455             else
00456                 spriteBatch.DrawString(fontBase, ssym, new Vector2(x + plused, y - 4), Color.White);
00457 
00458             if (charset.Contains(symbol))
00459                 spriteBatch.Draw(textureActivePlug, position, Color.White);
00460             else if (replace.ContainsKey(symbol))
00461             {
00462                 spriteBatch.Draw(textureActivePlug, position, Color.White);
00463                 int cval = BaseEnigma.code(replace[symbol]) + 1;
00464                 string csym = isNumber ? cval.ToString() : replace[symbol].ToString();
00465                 int cplused = isNumber ? (cval < 10 ? 0 : -3) : (replace[symbol] == 'I' ? 3 : 0);
00466                 spriteBatch.DrawString(fontMini, csym, new Vector2(position.X + 5 + cplused, position.Y + 40), Color.White);
00467             }
00468             else
00469                 spriteBatch.Draw(texturePlug, position, Color.Gray);
00470         }
