00493                 if (!sended)
00494                 {
00495                     charCurrent = symbol;
00496                     sended = true;
00497                     isSend = true;
00498                 }
00499             }
00500             else
00501             {
00502                 color = Color.White;
00503                 if (charCurrent == symbol)
00504                 {
00505                     charCurrent = charSend = '\0';
00506                     sended = false;
00507                     isSend = false;
00508                 }
00509             }
00510 
00511             spriteBatch.Draw(textureSymbol, rect, color);
00512             spriteBatch.DrawString(fontBase, symbol.ToString(), new Vector2(x + 11 + (symbol == 'I' ? 4 : 0), y + 8), color);
